import { useChat } from 'context';
import { notMe } from 'helpers';
import { Icon } from 'semantic-ui-react';

export const ChatList = () => {
  const {
    myChats,
    chatConfig,
    selectedChat,
    selectedChatClick,
    deleteChatClick,
  } = useChat();

  return (
    <div className="chat-list">
      {myChats.map((c, index) => (
        <div
          className={`chat-list-item ${
            selectedChat?.id === c.id ? 'selected-chat-item' : ''
          }`}
          key={index}
        >
          <div
            onClick={() => selectedChatClick(c)}
            className="chat-list-item-content"
          >
            {c.people.length === 1 ? (
              <>
                <Icon circular inverted color="violet" name="user cancel" />
                <div className="chat-list-preview">
                  <div className="preview-username">No Users Available</div>
                </div>
              </>
            ) : c.people.length === 2 ? (
              <>
                <>Avatar</>

                <div className="chat-list-preview">
                  <div className="preview-username">
                    {notMe(chatConfig, selectedChat)}
                  </div>
                  <div className="preview-message">
                    {c.last_message.attachments.length
                      ? `${c.last_message.sender.username} sent an attachments`
                      : c.last_message.text.slice(0 - 50) + '...'}
                  </div>
                </div>
              </>
            ) : (
              <>
                <Icon circular inverted color="brown" name="users" />

                <div className="preview-username">Bunch of people</div>
                <div className="preview-message">
                  {c.last_message.attachments.length
                    ? `${c.last_message.sender.username} sent an attachments`
                    : c.last_message.text.slice(0 - 50) + '...'}
                </div>
              </>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};
